import React, { useState } from 'react'
import "./LoginCss.css";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { useDispatch } from 'react-redux';
import { GetloginAction, LoginAction } from '../../Redux/Action/LoginAction';
import { useCookies } from 'react-cookie';
import { useNavigate } from 'react-router-dom';

function LoginForm() {

    const [cookies, setCookie, removeCookie] = useCookies(['token']);

    //  style
    const [falseData, setfalseData] = useState(false);

    const changeForm = () => {
        setfalseData(true)
    }
    const changeForm1 = () => {
        setfalseData(false)
    }

    // modal
    const [show, setShow] = useState(false);

    const handleClose = () =>{
        setShow(false);

    } 
    const handleShow = () =>{
        setShow(true);
    } 

   

    // code

    const [obj,setobj] = useState({})
    const [blank,setblank] = useState({})

    const [Loginobj,setLoginobj] = useState({})
    const [Loginblank,setLoginblank] = useState({})

    const dispatch = useDispatch();

    const ragisterData = (e)=> {
        if(e.target.name == "userImage"){
            obj.userImage = e.target.files[0];
            blank.userImage = "";
        }
        else{
            obj[e.target.name] = e.target.value;
            blank[e.target.name] = "";
        }

        setobj({...obj});
        
        
    }
    const handleSubmit = ()=> {
        setblank({...blank});

        const formData = new FormData();
        formData.append("userName",obj.userName)
        formData.append("email",obj.email)
        formData.append("mobile",obj.mobile)
        formData.append("gender",obj.gender)
        formData.append("birthDate",obj.birthDate)
        formData.append("city",obj.city)
        formData.append("state",obj.state )
        formData.append("country",obj.country)
        formData.append("postalCode",obj.postalCode);
        formData.append("officeContact",obj.officeContact);
        formData.append("qualification",obj.qualification );
        formData.append("address",obj.address);
        formData.append("password",obj.password);
        formData.append("confirmPassword",obj.confirmPassword);
        formData.append("userImage",obj.userImage);

        for(let x of formData.entries()){
            console.log(x); 
        }

       dispatch(GetloginAction(formData))
      

        setobj({...blank});

        setShow(false);
    }



    const navigate = useNavigate()
    // LOGINDATA

    const  LoginData=(e)=> {
        Loginobj[e.target.name] = e.target.value;
        Loginblank[e.target.name] = "";
        setLoginobj({...Loginobj})
    }

    const Login = async()=>{
        setLoginblank({...Loginblank});

        const res = await LoginAction(Loginobj);
        // res.token
        const date = new Date()
        date.setHours(date.getHours()+2);
        console.log(res)
        console.log(date)
        res &&  setCookie('token',  res.token, {path:"/", expires: date    });
         navigate("/home");
        
        setLoginobj({...Loginblank});
    }

    return (
        <>
            <div id="formMain">
                <div className="main">
                    <div id="mainCompo">
                        <div className="video-bg">
                            
                            
                            
                        </div>
                        <div className="dark-light">
                            <svg viewBox="0 0 24 24" stroke="currentColor" strokeWidth="1.5" fill="none" strokeLinecap="round" strokeLinejoin="round">
                                <path d="M21 12.79A9 9 0 1111.21 3 7 7 0 0021 12.79z" /></svg>
                        </div>

                    </div>
                    <div className={`container a-container  ${falseData ? "is-txl" : ""} `} id="a-container">
                        <form className="form" id="a-form" method="" action="">
                            <h2 className="form_title title">Login Account</h2>
                            <div className="form__icons">
                                <img className="form__icon" src="data:image/svg+xml;base64," alt="" />
                                <img className="form__icon"
                                    src="data:image/svg+xml;base64," />
                                <img
                                    className="form__icon"  src="data:image/svg+xml;base64," />
                            </div>

                            {/* LOGIN DATA  */}

                            <input className="form__input" name="email" type="text" placeholder="Email" value={Loginobj.email} onChange={LoginData}/>
                            <input className="form__input " name="password" style={{backgroundColor:"#ecf0f3"}} type="password" value={Loginobj.password} placeholder="Password" onChange={LoginData}/>

                            <button className="form__button button submit" type="button" onClick={Login}>Login</button>
                        </form>
                    </div>
                    <div className={`container b-container  ${falseData ? "is-txl " : ""}  ${falseData ? "is-z200" : ""}`} id="b-container">
                        <form className="form" id="b-form">
                            <h2 className="form_title title">Sign in to Website</h2>
                            <div className="form__icons">
                                <img className="form__icon"
                                    src="data:image/svg+xml;base64,"
                                    alt="" /><img className="form__icon"
                                        src="data:image/svg+xml;base64," />
                                <img
                                    className="form__icon"src="data:image/svg+xml;base64," />
                            </div>
                            <span className="form__span">or use your email account</span>

                             <Button variant="primary" className='form__button button submit' onClick={handleShow}>
                                Click To SIGN UP
                            </Button>

                        </form>
                    </div>
                    <div className={`switch ${falseData ? "is-txr" : ""}`} id="switch-cnt">
                        <div className={`switch__circle ${falseData ? "is-txr" : ""}`}></div>
                        <div className={`switch__circle switch__circle--t ${falseData ? "is-txr" : ""}`}></div>
                        <div className={`switch__container ${falseData ? "is-hidden" : ""}`} id="switch-c1">
                            <h2 className="switch__title title">Login Account !</h2>
                            <p className="switch__description description">To keep connected with us please login with your personal
                                info</p>
                            <button className="switch__button button switch-btn" onClick={() => changeForm()}>SIGN UP</button>
                        </div>
                        <div className={`switch__container ${!falseData ? "is-hidden" : ""}`} id="switch-c2">
                            <h2 className="switch__title title">Welcome Back !</h2>
                            <p className="switch__description description">Enter your personal details and start journey with us</p>
                            <button className="switch__button button switch-btn" onClick={() => changeForm1()}>SIGN IN</button>
                        </div>
                    </div>
                </div>
            </div>

        
         

      <Modal  backdrop="static" className='p-4'   keyboard={false}   size="lg" show={show} onHide={handleClose} >
        <Modal.Header closeButton style={{backgroundColor:"#ecf0f3"}}>
          <Modal.Title>SIGN UP</Modal.Title>
        </Modal.Header>
        <Modal.Body className='p-4'>


        <input className="form__input w-100 mb-3" value={obj.userName} type="text" placeholder="userName" name="userName" onChange={ragisterData}/>
        <input className="form__input w-100 mb-3" value={obj.email} type="email" placeholder="Email" name="email" onChange={ragisterData}/>
        <input className="form__input w-100 mb-3" value={obj.mobile} type="number" placeholder="Mobile" name="mobile" onChange={ragisterData}/>

       <div className='d-flex gap-3'>
        <select id="cars"  className="form__input w-50 mb-3" name='country'  onChange={ragisterData} >
                <option value="" disabled>Select country</option>
                <option value="India">India</option>
                <option value="Nepal">Nepal</option>
                <option value="Usa">Usa</option>
                <option value="Uk">Uk</option>
                <option value="Canada">Canada</option>
                <option value="Australia">Australia</option>
        </select>

       <select id="cars"  className="form__input w-50 mb-3" name='state'  onChange={ragisterData}>
                <option value="" className='text-light' disabled>Select state </option>
                <option value="Gujarat">Gujarat</option>
                <option value="Haryana">Haryana</option>
                <option value="Himachal_Pradesh">Himachal_Pradesh</option>
                <option value="Jharkhand" >Jharkhand</option>
                <option value="Karnataka">Karnataka</option>
                <option value="Kerala">Kerala</option>
                <option value="Chhattisgarh">Chhattisgarh</option>
                <option value="Goa">Goa</option>

                {/* Chhattisgarh, Goa, Gujarat, Haryana, Himachal_Pradesh, Jharkhand, Karnataka, Kerala, */}
        </select>

       </div>


        <input className="form__input w-100 mb-3" type="text" value={obj.city} placeholder="City" name="city" onChange={ragisterData}/>



      <div className='d-flex gap-3'>
      <input className="form__input w-50 mb-3 " type="date" value={obj.birthDate} name='birthDate' placeholder="birthDate" onChange={ragisterData}/>
        <input className="form__input w-50 mb-3 " type="text" value={obj.qualification} name='qualification' placeholder="qualification" onChange={ragisterData}/>
      </div>


      <div className='d-flex gap-3'>
      <input className="form__input w-50 mb-3 " type="number" value={obj.officeContact} name='officeContact' placeholder="OfficeContact" onChange={ragisterData}/>
        <input className="form__input w-50 mb-3 " type="number" value={obj.postalCode} name='postalCode' placeholder="PostalCode" onChange={ragisterData}/>
      </div>

     <div className='d-flex gap-3'>
     <input className="form__input w-50  mb-3 " type="password" value={obj.password} name='password' placeholder="Password" onChange={ragisterData}/>
        <input className="form__input w-50  mb-3 " type="password" value={obj.confirmPassword} name='confirmPassword' placeholder="ConfirmPassword" onChange={ragisterData}/>
     </div>

        <textarea className="form__input w-100 mb-3  pt-2 " type="text" value={obj.address} name='address' placeholder="Address" onChange={ragisterData}  rows="20" />

        <div className='w-100 text-start ps-2 mb-3'>
            <div className='w-100 text-start  '>
                <span className='fs-6'>Gender :</span>
                <input type="radio" className='me-2 ms-1' value={"male"} name='gender' checked={obj.gender === "male"} onChange={ragisterData} />Male
                <input type="radio" className='me-2 ms-1' value={"Female"} name='gender' checked={obj.gender === "Female"} onChange={ragisterData} />Female
            </div>
            
        </div>


        <input className="ps-2" type="file"  name='userImage'  onChange={ragisterData}/>

        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button  onClick={handleSubmit}  className=' bnt btn-success  form__button button submit'>
            Submit
          </Button>
        </Modal.Footer>
      </Modal>

        </>
    )
}

export default LoginForm